n\": \"4.8.2\",\n  \"author\": \"WP Engine\",\n  \"author_profile\": \"https://profiles.wordpress.org/wpengine/\",\n  \"contributors\": {\n    \"justlevine\": {\n      \"profile\": \"https://profiles.wordpress.org/justlevine/\",\n      \"avatar\": \"https://secure.gravatar.com/avatar/33212bff2915bd72792772a2d3203abe0402352b6be6ba65081036f10560c6e6?r=g\",\n      \"display_name\": \"justlevine\"\n    },\n    \"wpengine\": {\n      \"profile\": \"https://profiles.wordpress.org/wpengine/\",\n      \"avatar\": \"https://secure.gravatar.com/avatar/6d44461b684d2f3d8f6fcf3d657cb889?s=96&d=monsterid&r=g\",\n      \"display_name\": \"WP Engine\"\n    }\n  },\n  \"requires\": \"5.7\",\n  \"tested\": \"6.7.2\",\n  \"requires_php\": \"7.4\",\n  \"requires_plugins\": [],\n  \"rating\": 100,\n  \"ratings\": {\n    \"5\": 1,\n    \"4\": 0,\n    \"3\": 0,\n    \"2\": 0,\n    \"1\": 0\n  },\n  \"num_ratings\": 1,\n  \"support_url\": \"https://github.com/wpengine/wp-graphql-content-blocks/issues\",\n  \"support_threads\": 0,\n  \"support_threads_resolved\": 0,\n  \"active_installs\": 10,\n  \"last_updated\": \"2025-03-05 18:23 GMT\",\n  \"added\": \"2025-02-10\",\n  \"homepage\": \"https://github.com/wpengine/wp-graphql-content-blocks/\",\n  \"sections\": {\n    \"description\": \"WordPress plugin that extends WPGraphQL to support querying (Gutenberg) Blocks as data.\",\n    \"changelog\": \"<h4>4.8.2</h4><li>Fixes fatal error for the Site Health info page for Semver checks.</li><h4>4.8.1</h4><li>Fixes fatal error when you de-activate WPGraphQL</li> <h4>4.8.0</h4> <li>Querying Object-Type Block Attributes in WPGraphQL</li> <h4>4.7.0</h4> <li>Adds support for resolving and returning related term items as a terms connection for the CorePostTerms block along with taxonomy connection. Adds support for resolving and returning the prefix and suffix items within the correspondent fields of the CorePostTerms block.</li>\",\n    \"installation\": \"https://faustjs.org/tutorial/get-started-with-wp-graphql-content-blocks\",\n    \"reviews\": \"\"\n  },\n  \"download_link\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.8.2.zip\",\n  \"upgrade_notice\": [],\n  \"screenshots\": {},\n  \"tags\": {},\n  \"versions\": {\n    \"4.6.0\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.6.0.zip\",\n    \"4.7.0\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.7.0.zip\",\n    \"4.8.0\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.8.0.zip\",\n    \"4.8.1\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.8.1.zip\",\n    \"4.8.2\": \"https://wpe-plugin-updates.wpengine.com/wp-graphql-content-blocks/wp-graphql-content-blocks.4.8.2.zip\"\n  },\n  \"business_model\": false,\n  \"repository_url\": \"\",\n  \"commercial_support_url\": \"\",\n  \"donate_link\": \"\",\n  \"banners\": {},\n  \"preview_link\": \"\",\n  \"stable_tag\": \"4.8.2\",\n  \"pluginURI\": \"https://developers.wpengine.com/\",\n  \"description\": \"WordPress plugin that extends WPGraphQL to support querying (Gutenberg) Blocks as data.\",\n  \"authorURI\": \"https://wpengine.com/\",\n  \"textDomain\": \"wp-graphql-content-blocks\",\n  \"network\": \"\"\n}\n','off'),(150,'theme_mods_twentytwentyfive','a:1:{s:18:\"custom_css_post_id\";i:-1;}','auto'),(151,'_transient_wp_styles_for_blocks','a:2:{s:4:\"hash\";s:32:\"6a64f06d5174183821b9e5e1b0e89c36\";s:6:\"blocks\";a:52:{s:11:\"core/button\";s:0:\"\";s:14:\"core/site-logo\";s:0:\"\";s:18:\"core/post-template\";s:0:\"\";s:12:\"core/columns\";s:769:\":root :where(.wp-block-columns-is-layout-flow) > :first-child{margin-block-start: 0;}:root :where(.wp-block-columns-is-layout-flow) > :last-child{margin-block-end: 0;}:root :where(.wp-block-columns-is-layout-flow) > *{margin-block-start: var(--wp--preset--spacing--50);mar