s:6:\"height\";i:576;s:9:\"mime-type\";s:10:\"image/jpeg\";s:8:\"filesize\";i:132969;}s:9:\"1536x1536\";a:5:{s:4:\"file\";s:41:\"dabbas-j0OhftYAXR4-unsplash-1536x1153.jpg\";s:5:\"width\";i:1536;s:6:\"height\";i:1153;s:9:\"mime-type\";s:10:\"image/jpeg\";s:8:\"filesize\";i:440313;}s:9:\"2048x2048\";a:5:{s:4:\"file\";s:41:\"dabbas-j0OhftYAXR4-unsplash-2048x1537.jpg\";s:5:\"width\";i:2048;s:6:\"height\";i:1537;s:9:\"mime-type\";s:10:\"image/jpeg\";s:8:\"filesize\";i:685580;}}s:10:\"image_meta\";a:12:{s:8:\"aperture\";s:1:\"0\";s:6:\"credit\";s:0:\"\";s:6:\"camera\";s:0:\"\";s:7:\"caption\";s:0:\"\";s:17:\"created_timestamp\";s:1:\"0\";s:9:\"copyright\";s:0:\"\";s:12:\"focal_length\";s:1:\"0\";s:3:\"iso\";s:1:\"0\";s:13:\"shutter_speed\";s:1:\"0\";s:5:\"title\";s:0:\"\";s:11:\"orientation\";s:1:\"0\";s:8:\"keywords\";a:0:{}}s:14:\"original_image\";s:31:\"dabbas-j0OhftYAXR4-unsplash.jpg\";}'),(162,309,'_thumbnail_id','356'),(163,301,'_thumbnail_id','342'),(164,28,'_wp_trash_meta_status','publish'),(165,28,'_wp_trash_meta_time','1743165634'),(166,28,'_wp_desired_post_slug','taxonomy_67e68296e2309'),(178,358,'_acf_changed',''),(179,358,'footnotes',''),(180,359,'_acf_changed',''),(181,359,'footnotes',''),(182,360,'_acf_changed',''),(183,361,'_acf_changed',''),(187,363,'_acf_changed',''),(188,363,'footnotes',''),(195,365,'_acf_changed',''),(196,365,'footnotes',''),(197,366,'_acf_changed','');
